import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Navbar from './Navbar';
import Sidenav from './Sidenav';

const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const handlePrev = () => {
    setCurrentSlide(currentSlide === 0 ? numSlides - 1 : currentSlide - 1);
  };

  const handleNext = () => {
    setCurrentSlide(currentSlide === numSlides - 1 ? 0 : currentSlide + 1);
  };

  const slides = [
    {
      id: 1,
      image: '/assets/imgs/ingame/aralin.png',
      alt: 'Aralin Image',
      className: 'aralin',
    },
    {
      id: 2,
      image: '/assets/imgs/ingame/pagsusulit.png',
      alt: 'Pagsusulit Image',
      className: 'pagsusulit',
    },
    // Add more slides as needed
  ];

  const numSlides = slides.length;

  return (
    <>
      <div>
        <Navbar />
        <Sidenav />
      </div>
      <div className='home-carousel'>
        <img src='/assets/imgs/ingame/prev.png' id='prev-image' onClick={handlePrev} />
        {slides.map((slide, index) => (
          <div key={slide.id} style={{ display: index === currentSlide ? 'block' : 'none' }}>
            <img src={slide.image} className='gamebt' id={`${slide.className}-image`} alt={slide.alt} />
            <img src={`/assets/imgs/ingame/${slide.className}1.png`} className={slide.className} alt={slide.alt} />
          </div>
        ))}
        <img src='/assets/imgs/ingame/next.png' id='next-image' onClick={handleNext} />
      </div>
      <img src='/assets/imgs/ingame/levels.png' id='level-image' />
    </>
  );
};

export default function Home() {
  return <Carousel />;
}
