import React, { useState } from 'react';
import { useEffect } from 'react'; // Import useEffect if needed
import Navbar from './Navbar';
import Sidenav from './Sidenav';

export default function Home() {
  // State to keep track of the current image index
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Array of image paths for your carousel
  const images = [
    '/assets/imgs/ingame/aralin1.png',
    '/assets/imgs/ingame/pagsusulit1.png',
    '/assets/imgs/ingame/aralin2.png',
    '/assets/imgs/ingame/pagsusulit2.png',
    // Add more image paths as needed
  ];

  // Function to handle going to the next image
  const goToNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  // Function to handle going to the previous image
  const goToPrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  // Use useEffect to automatically transition to the next image after a certain time
  useEffect(() => {
    const interval = setInterval(goToNextImage, 5000); // Change the interval time as needed
    return () => clearInterval(interval); // Cleanup the interval on component unmount
  }, []);

  return (
    <>
      <div>
        <Navbar />
        <Sidenav />
      </div>

      <div className='home-carousel'>
        <img
          src='/assets/imgs/ingame/prev.png'
          id='prev-image'
          onClick={goToPrevImage}
        />

        <div>
          <img
            src={images[currentImageIndex]}
            className='gamebt'
            alt={`Image ${currentImageIndex}`}
          />
        </div>

        <img
          src='/assets/imgs/ingame/next.png'
          id='next-image'
          onClick={goToNextImage}
        />
      </div>

      <img src='/assets/imgs/ingame/levels.png' id='level-image' />
    </>
  );
}
