import React, { useState } from 'react';

const Home = () => {
  const [currentImage, setCurrentImage] = useState(1); // State to track current image (1 for aralin, 2 for pagsusulit)

  const handleNext = () => {
    setCurrentImage(currentImage === 1 ? 2 : 1); // Switch between aralin and pagsusulit images
  };

  const handlePrev = () => {
    setCurrentImage(currentImage === 1 ? 2 : 1); // Switch between aralin and pagsusulit images
  };

  return (
    <>
      <div className='home-carousel'>
        <button onClick={handlePrev}>&lt;</button>
        <div>
          {currentImage === 1 && (
            <>
              <img src="/assets/imgs/ingame/aralin1.png" className="aralin" alt="AralinImage" />
              {/* Add any additional content for 'aralin' if needed */}
            </>
          )}
          {currentImage === 2 && (
            <>
              <img src="/assets/imgs/ingame/pagsusulit1.png" className="pagsusulit" alt="PagsusulitImage" />
              {/* Add any additional content for 'pagsusulit' if needed */}
            </>
          )}
        </div>
        <button onClick={handleNext}>&gt;</button>
      </div>
    </>
  );
};

export default Home;
